mapper.select:
select rnum, pid, name, description, category, price
from (
    select rownum as rnum, pid, name, description, category, price
    from (
       select pid, name, description, category, price 
       from products
       order by pid desc
    )
    where rownum <= #{endRowNo}
) 
where rnum >= #{startRowNo}

select pid, name, description, category, price 
from products
where pid=#{pid}
